  event ExecuteForWallet(
    address indexed contractAddress,
    uint256 tokenId,
    string walletManagerId,
    address indexed externalAddress,
    bytes encodedParams,
    uint256 ethValue
  );
  event ExecuteForBasket(
    address indexed contractAddress,
    uint256 tokenId,
    string basketManagerId,
    address indexed externalAddress,
    bytes encodedParams,
    uint256 ethValue
  );
  event PrincipalRefreshed(
    address contractAddress,
    uint256 tokenId,
    string walletManagerId,
    address assetToken
  );
