pragma solidity ^0.8.13;

import {IERC165} from "@openzeppelin/contracts/utils/introspection/IERC165.sol";
import {IERC721} from "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import {IERC1271} from "@openzeppelin/contracts/interfaces/IERC1271.sol";

library NftTokenInfo {
  bytes4 constant internal INTERFACE_SIGNATURE_ERC721 = 0x80ac58cd;
  bytes4 constant internal INTERFACE_SIGNATURE_ERC1155 = 0xd9b67a26;

  function isERC721(address contractAddress) internal view returns (bool) {
    return IERC165(contractAddress).supportsInterface(INTERFACE_SIGNATURE_ERC721);
  }

  function isERC1155(address contractAddress) internal view returns (bool) {
    return IERC165(contractAddress).supportsInterface(INTERFACE_SIGNATURE_ERC1155);
  }

  function getTokenUUID(address contractAddress, uint256 tokenId) internal pure returns (uint256) {
    return uint256(keccak256(abi.encodePacked(contractAddress, tokenId)));
  }

  function getTokenOwner(address contractAddress, uint256 tokenId) internal returns (address) {
    return _getTokenOwner(contractAddress, tokenId);
  }

  function isNFTOwnerOrOperator(address contractAddress, uint256 tokenId, address sender) internal returns (bool) {
    IERC721 tokenInterface = IERC721(contractAddress);
    address tokenOwner = _getTokenOwner(contractAddress, tokenId);
    return (sender == tokenOwner || tokenInterface.isApprovedForAll(tokenOwner, sender));
  }

  function _getTokenOwner(address contractAddress, uint256 tokenId) internal returns (address) {
    // solhint-disable-next-line
    (bool success, bytes memory returnData) = contractAddress.call(abi.encodeWithSelector(IERC721.ownerOf.selector, tokenId));
    if (success) {
      return abi.decode(returnData, (address));
    } else {
      return address(0x0);
    }
  }
}

contracts/lib/SmartAccountBase.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.13;

import {IERC165, ERC165} from "@openzeppelin/contracts/utils/introspection/ERC165.sol";
import {IERC721} from "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import {IERC1271} from "@openzeppelin/contracts/interfaces/IERC1271.sol";
import {IERC721Receiver} from "@openzeppelin/contracts/token/ERC721/IERC721Receiver.sol";
import {IERC1155Receiver} from "@openzeppelin/contracts/token/ERC1155/IERC1155Receiver.sol";
import {SignatureChecker} from "@openzeppelin/contracts/utils/cryptography/SignatureChecker.sol";

import {IERC6551Account} from "../interfaces/IERC6551Account.sol";
import {IERC6551Executable} from "../interfaces/IERC6551Executable.sol";
import {ERC6551AccountLib} from "./ERC6551AccountLib.sol";

import {ISmartAccount} from "../interfaces/ISmartAccount.sol";
import {ISmartAccountController} from "../interfaces/ISmartAccountController.sol";

// import "hardhat/console.sol";

error AlreadyInitialized();
error NotAuthorized();
error InvalidInput();
error OwnershipCycle();

/**
 * @title A smart contract account owned by a single ERC721 token
 */
abstract contract SmartAccountBase is ISmartAccount, ERC165 {
  address internal _chargedParticles;
  address internal _executionController;

  /// @dev mapping from owner => caller => has permissions
  mapping(address => mapping(address => bool)) internal _permissions;

  bool internal _initialized;
  constructor() {}

  function initialize(address chargedParticles, address executionController) external {
    if (_initialized) { revert AlreadyInitialized(); }
    _initialized = true;
    _chargedParticles = chargedParticles;
    _executionController = executionController;
  }

  /// @dev allows eth transfers by default, but allows account owner to override
  receive() external payable virtual override {}

  function isInitialized() external view virtual override returns (bool) {
    return _initialized;
  }

  function permissions(address _owner, address caller) public view virtual returns (bool) {
    return _permissions[_owner][caller];
  }

  function getChargedParticles() public view virtual returns (address) {
    return _chargedParticles;
  }

  function setChargedParticles(address chargedParticles) public virtual onlyOwner {
    if (chargedParticles == address(0)) { revert InvalidInput(); }
    _chargedParticles = chargedParticles;
  }

  function getExecutionController() public view virtual returns (address) {
    return _executionController;
  }

  function setExecutionController(address executionController) public virtual onlyOwner {
    _executionController = executionController;
    emit ExecutionControllerUpdated(msg.sender, executionController);
  }

  /// @dev Returns the EIP-155 chain ID, token contract address, and token ID for the token that
  /// owns this account.
  function token()
    public
    view
    virtual
    returns (
      uint256 chainId,
      address tokenContract,
      uint256 tokenId
    )
  {
    return ERC6551AccountLib.token();
  }

  /// @dev Returns the owner of the ERC-721 token which owns this account. By default, the owner
  /// of the token has full permissions on the account.
  function owner() public view virtual returns (address) {
    (uint256 chainId, address tokenContract, uint256 tokenId) = ERC6551AccountLib.token();
    if (chainId != block.chainid) { return address(0); }

    try IERC721(tokenContract).ownerOf(tokenId) returns (address _owner) {
      return _owner;
    } catch {
      return address(0);
    }
  }

  function isValidSigner(address signer, bytes calldata) external view virtual returns (bytes4) {
    if (_isValidSigner(signer)) {
      return IERC6551Account.isValidSigner.selector;
    }
    return bytes4(0);
  }

  function isValidSignature(bytes32 hash, bytes memory signature)
    external
    view
    virtual
    returns (bytes4 magicValue)
  {
    bool isValid = SignatureChecker.isValidSignatureNow(owner(), hash, signature);
    if (isValid) {
      return IERC1271.isValidSignature.selector;
    }
    return bytes4(0);
  }

  /// @dev grants a given caller execution permissions
  function setPermissions(address[] calldata callers, bool[] calldata newPermissions) public virtual {
    address _owner = owner();
    if (msg.sender != _owner) { revert NotAuthorized(); }

    uint256 length = callers.length;
    if (newPermissions.length != length) { revert InvalidInput(); }

    for (uint256 i = 0; i < length; i++) {
      _permissions[_owner][callers[i]] = newPermissions[i];
      emit PermissionUpdated(_owner, callers[i], newPermissions[i]);
    }
  }

  /// @dev Returns true if a given interfaceId is supported by this account. This method can be
  /// extended by an override.
  function supportsInterface(bytes4 interfaceId)
    public
    view
    virtual
    override(IERC165, ERC165)
    returns (bool)
  {
    return interfaceId == type(IERC6551Account).interfaceId
      || interfaceId == type(IERC6551Executable).interfaceId
      || interfaceId == type(ISmartAccount).interfaceId
      || super.supportsInterface(interfaceId);
  }

  /// @dev Allows ERC-721 tokens to be received so long as they do not cause an ownership cycle.
  /// This function can be overriden.
  function onERC721Received(
    address,
    address,
    uint256 receivedTokenId,
    bytes memory
  ) public view virtual override returns (bytes4) {
    (
      uint256 chainId,
      address tokenContract,
      uint256 tokenId
    ) = ERC6551AccountLib.token();

    if (chainId == block.chainid && tokenContract == msg.sender && tokenId == receivedTokenId) {
      revert OwnershipCycle();
    }
    return this.onERC721Received.selector;
  }

  /// @dev Allows ERC-1155 tokens to be received. This function can be overriden.
  function onERC1155Received(
    address,
    address,
    uint256,
    uint256,
    bytes memory
  ) public pure virtual override returns (bytes4) {
    return this.onERC1155Received.selector;
  }

  /// @dev Allows ERC-1155 token batches to be received. This function can be overriden.
  function onERC1155BatchReceived(
    address,
    address,
    uint256[] memory,
    uint256[] memory,
    bytes memory
  ) public pure virtual override returns (bytes4) {
    return this.onERC1155BatchReceived.selector;
  }

  /// @dev Executes a low-level call
  function _call(
    address to,
    uint256 value,
    bytes calldata data
  ) internal returns (bytes memory result) {
    bool success;
    // solhint-disable-next-line avoid-low-level-calls
    (success, result) = to.call{value: value}(data);

    if (!success) {
      assembly {
        revert(add(result, 32), mload(result))
      }
    }
  }

  function _onExecute(
    address to,
    uint256 value,
    bytes calldata data,
    uint8 operation
  ) internal {
    if (IERC165(_executionController).supportsInterface(type(ISmartAccountController).interfaceId)) {
      string memory revertReason = ISmartAccountController(_executionController).onExecute(to, value, data, operation);
      if (bytes(revertReason).length > 0) {
        revert(revertReason);
      }
    }
  }

  function _onUpdateToken(
    bool isReceiving,
    address assetToken,
    uint256 assetAmount
  ) internal {
    if (IERC165(_executionController).supportsInterface(type(ISmartAccountController).interfaceId)) {
      (uint256 chainId, address tokenContract, uint256 tokenId) = ERC6551AccountLib.token();
      ISmartAccountController(_executionController)
        .onUpdateToken(isReceiving, chainId, tokenContract, tokenId, assetToken, assetAmount);
    }
  }

  function _onUpdateNFT(
    bool isReceiving,
    address childTokenContract,
    uint256 childTokenId,
    uint256 childTokenAmount
  ) internal {
    if (IERC165(_executionController).supportsInterface(type(ISmartAccountController).interfaceId)) {
      (uint256 chainId, address tokenContract, uint256 tokenId) = ERC6551AccountLib.token();
      ISmartAccountController(_executionController)
        .onUpdateNFT(isReceiving, chainId, tokenContract, tokenId, childTokenContract, childTokenId, childTokenAmount);
    }
  }

  function _onUpdateNFTBatch(
    bool isReceiving,
    address childTokenContract,
    uint256[] calldata childTokenIds,
    uint256[] calldata childTokenAmounts
  ) internal {
    if (IERC165(_executionController).supportsInterface(type(ISmartAccountController).interfaceId)) {
      (uint256 chainId, address tokenContract, uint256 tokenId) = ERC6551AccountLib.token();
      ISmartAccountController(_executionController)
        .onUpdateNFTBatch(isReceiving, chainId, tokenContract, tokenId, childTokenContract, childTokenIds, childTokenAmounts);
    }
  }

  function _isValidSigner(address signer) internal view virtual returns (bool) {
    address ownerOf = owner();

    // Charged Particles always has permissions
    if (signer == _chargedParticles) { return true; }

    // authorize caller if owner has granted permissions
    if (_permissions[ownerOf][signer]) { return true; }

    // authorize token owner
    return signer == ownerOf;
  }

  /// @dev reverts if caller is not the owner of the NFT which owns the account
  modifier onlyOwner() {
    if (msg.sender != owner()) { revert NotAuthorized(); }
    _;
  }

  /// @dev reverts if caller is not authorized to execute on this account
  modifier onlyValidSigner() {
    if (!_isValidSigner(msg.sender)) { revert NotAuthorized(); }
    _;
  }
}

contracts/SmartAccount.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.13;

import {IERC165} from "@openzeppelin/contracts/utils/introspection/IERC165.sol";
import {SmartAccountBase, NotAuthorized, InvalidInput} from "./lib/SmartAccountBase.sol";

/**
 * @title A smart contract account owned by a single ERC721 token
 */
contract SmartAccount is SmartAccountBase {
  uint256 public state;

  constructor() SmartAccountBase() {}


  /// @dev allows eth transfers by default
  receive() external payable virtual override {}

  /// @dev executes a low-level call against an account if the caller is authorized to make calls
  function execute(
    address to,
    uint256 value,
    bytes calldata data,
    uint8 operation
  )
    public
    payable
    virtual
    override
    onlyValidSigner
    returns (bytes memory)
  {
    require(operation == 0, "Only call operations are supported");
    ++state;

    // Perform custom checks/updates from within a custom controller
    _onExecute(to, value, data, operation);

    // Execute Call on Account
    return _call(to, value, data);
  }


  function handleTokenUpdate(
    bool isReceiving,
    address assetToken,
    uint256 assetAmount
  )
    public
    virtual
    override
    onlyValidSigner
  {
    // Perform custom checks/updates from within a custom controller
    _onUpdateToken(isReceiving, assetToken, assetAmount);
  }

  function handleNFTUpdate(
    bool isReceiving,
    address tokenContract,
    uint256 tokenId,
    uint256 tokenAmount
  )
    public
    virtual
    override
    onlyValidSigner
  {
    // Perform custom checks/updates from within a custom controller
    _onUpdateNFT(isReceiving, tokenContract, tokenId, tokenAmount);
  }

  function handleNFTBatchUpdate(
    bool isReceiving,
    address tokenContract,
    uint256[] calldata tokenIds,
    uint256[] calldata tokenAmounts
  )
    public
    virtual
    override
    onlyValidSigner
  {
    // Perform custom checks/updates from within a custom controller
    _onUpdateNFTBatch(isReceiving, tokenContract, tokenIds, tokenAmounts);
  }
}

contracts/tokens/BufficornZK.sol.content:
// SPDX-License-Identifier: MIT
// Written by: Rob Secord (https://twitter.com/robsecord)
// Co-founder @ Charged Particles - Visit: https://charged.fi
// Co-founder @ Taggr             - Visit: https://taggr.io

pragma solidity ^0.8.13;

import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";
import {IERC165} from "@openzeppelin/contracts/utils/introspection/ERC165.sol";
import {ERC721, ERC721Enumerable} from "@openzeppelin/contracts/token/ERC721/extensions/ERC721Enumerable.sol";
import {Strings} from "@openzeppelin/contracts/utils/Strings.sol";
import {ISmartAccountController} from "../interfaces/ISmartAccountController.sol";
import {IDynamicTraits} from "../interfaces/IDynamicTraits.sol";

/**
 * @dev todo...
 */
contract BufficornZK is ISmartAccountController, IDynamicTraits, Ownable, ERC721Enumerable {
  using Strings for uint256;

  // Base portion of the Token Metadata URI (format: base-uri/tokenId/traitsMap)
  string internal _tokenUriBase;

  // TokenId => Traits BitMap
  mapping (uint256 => uint256) internal _traitBits;

  // ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
  // Initialization

  constructor(string memory name, string memory symbol)
    ERC721(name, symbol) Ownable() {}


  // ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
  // Minting Logic

  // For minting container-NFTs that have no initial traits
  function mint(uint256 tokenId) external virtual {
    _mint(tokenId, 0);
  }

  // For minting child-NFTs that have initial fixed traits
  function mintWithTraits(uint256 tokenId, uint256 traits) external virtual {
    _mint(tokenId, traits);
  }


  // ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
  // Customizable Traits Logic

  function getTraits(uint256 tokenId) external view override returns (uint256) {
    return _traitBits[tokenId];
  }

  function hasTrait(uint256 tokenId, uint256 trait) external view override returns (bool) {
    uint256 bit = _traitBits[tokenId] & (1 << trait);
    return bit > 0;
  }

  function traitCount(uint256 tokenId) external view override returns (uint256 totalTraits) {
    uint256 map = _traitBits[tokenId];
    // Brian Kerninghan bit-counting method = O(log(n))
    while (map != 0) {
      map &= (map - 1);
      totalTraits += 1;
    }
  }

  function _addTraits(uint256 tokenId, uint256 traits) internal returns (uint256) {
    _traitBits[tokenId] |= traits;
    return _traitBits[tokenId];
  }

  function _removeTraits(uint256 tokenId, uint256 traits) internal returns (uint256) {
    uint256 mask = traits ^ (2 ** 256 - 1); // negate to find the traits to keep
    _traitBits[tokenId] &= mask;
    return _traitBits[tokenId];
  }


  // ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
  // Standard NFT Logic

  function tokenURI(uint256 tokenId) public view virtual override returns (string memory) {
    _requireMinted(tokenId);
    string memory baseURI = _baseURI();
    return bytes(baseURI).length > 0 ? string(abi.encodePacked(baseURI, tokenId.toString(), "/", _traitBits[tokenId].toString())) : "";
  }

  function setBaseURI(string memory newBase) external onlyOwner {
    _tokenUriBase = newBase;
  }

  function _baseURI() internal view virtual override returns (string memory) {
    return _tokenUriBase;
  }


  // ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
  // SmartAccount Controller Logic

  function onExecute(
    address,
    uint256,
    bytes calldata,
    uint8
  )
    external
    virtual
    override
    returns (string memory revertReason)
  {
    return ""; // success
  }

  function onUpdateToken(
    bool isReceiving,
    uint256, // this chain
    address, // this contract
    uint256 tokenId,
    address receivedAssetToken,
    uint256 receivedAssetAmount
  )
    external
    virtual
    override
  {
    // no-op
  }

  function onUpdateNFT(
    bool isReceiving,
    uint256, // this chain
    address, // this contract
    uint256 tokenId,
    address receivedTokenContract,
    uint256 receivedTokenId,
    uint256
  )
    external
    virtual
    override
  {
    if (IERC165(receivedTokenContract).supportsInterface(type(IDynamicTraits).interfaceId)) {
      uint256 newTraits = IDynamicTraits(receivedTokenContract).getTraits(receivedTokenId);
      if (isReceiving) {
        _addTraits(tokenId, newTraits);
      } else {
        _removeTraits(tokenId, newTraits);
      }
    }
  }

  function onUpdateNFTBatch(
    bool isReceiving,
    uint256, // this chain
    address, // this contract
    uint256 tokenId,
    address receivedTokenContract,
    uint256[] calldata receivedTokenIds,
    uint256[] calldata
  )
    external
    virtual
    override
  {
    uint256 i;
    uint256 t;
    uint256 n = receivedTokenIds.length;
    if (IERC165(receivedTokenContract).supportsInterface(type(IDynamicTraits).interfaceId)) {
      for (; i < n; i++) {
        t = IDynamicTraits(receivedTokenContract).getTraits(receivedTokenIds[i]);
        if (isReceiving) {
          _addTraits(tokenId, t);
        } else {
          _removeTraits(tokenId, t);
        }
      }
    }
  }


  // ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
  // Custom Interfaces

  /// @dev Returns true if a given interfaceId is supported by this account. This method can be
  /// extended by an override.
  function supportsInterface(bytes4 interfaceId)
    public
    view
    virtual
    override(IERC165, ERC721Enumerable)
    returns (bool)
  {
    return
      interfaceId == type(ISmartAccountController).interfaceId ||
      interfaceId == type(IDynamicTraits).interfaceId ||
      super.supportsInterface(interfaceId);
  }


  // ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
  // Internal Functions

  function _mint(uint256 tokenId, uint256 traits) internal {
    // Note: Do NOT set traits when bridging a Bufficorn-NFT.
    //       The "traits" param is ONLY used for minting Trait-NFTs
    //       which can be minted from this contract, or another contract.
    //       When bridging a Bufficorn, this contract will retain the last-known
    //       state for a Bufficorns nested traits on zkSync.
    if (traits > 0) {
      _traitBits[tokenId] = traits;
    }
    _safeMint(_msgSender(), tokenId);
  }
}
